INSERT INTO `requests` (`id`, `Instrument`, `ECsession`, `Addressee`, `Request`, `Deadline`, `Status`, `Verification`) VALUES
(1, 'Resolution 1 (EC-73)', 'EC-73', 'C/HCP', 'Requests the Chair of HCP, with support from the WMO Secretariat, to facilitate a consultative process with the Permanent Representatives, Presidents of Regional Associations and Technical Commissions, Chair of the Research Board, Chair HCP and National Hydrological Advisers for the purpose of finalizing and familiarizing the above-mentioned parties with the draft Declaration for consideration at the Extraordinary session of the World Meteorological Congress in 2021 (Cg-Ext.(2021)).', 'No Deadline', '', ''),
(2, 'Resolution 2 (EC-73)', 'EC-73', 'SERCOM', 'Requests the Commission for Weather, Climate, Water and Related Environmental Services and Applications (SERCOM) to:\n(1) Develop a full Implementation Plan articulating roles and responsibilities of National Meteorological and Hydrological Services (NMHSs) and Regional Specialized Meteorological Centres (RSMCs), based on the IP outline provided in the annex to the present resolution, led by the Standing Committee on Disaster Risk Reduction and Public Services (SC-DRR), in consultation with the Regional Associations (RAs), the Commission for Observation, Infrastructures and Information Systems (INFCOM) and other relevant bodies;\n(2) Guide the development process of the full IP to appropriately take into consideration gaps and challenges identified through the two pilot projects in RA VI (Europe) and RA V (South-West Pacific), defined roles and responsibilities of Global Data-Processing and Forecasting System (GDPFS) Centres and their available resources in relation to other relevant WMO initiatives;\n(3) Oversee the implementation of, and provide recommendations for, the revision as necessary of the methodology for WMO-CHE in collaboration with Members and the RAs;\n(4) Collaborate with INFCOM on developing regulatory material and guidance for operational implementation;\n(5) Collaborate with the Research Board (RB) on scientific matters that relate to identification, attribution, hazards (including extreme events) and their impacts on societies;\n(6) Collaborate with the RAs on the implementation and refinement of the methodology based on practical experiences to ensure the WMO Catalogue will best incorporate events from all regions, leaving none of them underreported;\n(7) Collaborate with the relevant United Nations agencies, the private sector, such as insurance and reinsurance companies, and other relevant organizations, to facilitate the implementation and coordination of loss and damage accounting methodologies;', 'No Deadline', '', ''),
(3, 'Resolution 2 (EC-73)', 'EC-73', 'RAs', 'Requests the RAs to identify relevant mechanisms to contribute to the further elaboration of the design and implementation of WMO-CHE;', 'No Deadline', '', ''),
(4, 'Resolution 2 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to facilitate the necessary interactions among the above-mentioned stakeholders.', 'No Deadline', '', ''),
(5, 'Resolution 3 (EC-73)', 'EC-73', 'SERCOM', 'Requests the Commission for Weather, Climate, Water and Related Environmental Services and Applications (SERCOM) to further develop the concept of the Global Drought Classification System and develop an implementation plan to test the concept, led by the Standing Committee on Services for Agriculture (SC-AGR) in coordination with the Standing Committee on Hydrological Services (SC-HYD), the Standing Committee on Climate Services (SC-CLI) and other relevant bodies as appropriate including with the World Climate Research Programme on aspects of long-term changes and future projections of drought and the relevant mechanisms of the United Nations Convention to Combat Desertification (UNCCD);', 'No Deadline', '', ''),
(6, 'Resolution 4 (EC-73)', 'EC-73', 'SERCOM', 'Invites Services Commission (SERCOM) in close collaboration with the Infrastructure Commission (INFCOM) to lead the coordination of the roadmap activities and engage with the appropriate experts;', 'No Deadline', '', ''),
(7, 'Resolution 4 (EC-73)', 'EC-73', 'Secretariat', 'Entrusts the Secretariat to liaise with the presidents of the technical commissions and the Chair of the Research Board to nominate relevant experts in the Advisory Board for the recognition of long-term observing stations with the terms of reference described in Annex 1;', 'No Deadline', '', ''),
(8, 'Resolution 4 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to further promote WMO recognition of long-term observing stations including resource mobilization campaigns to support the digitization of their time-series data, where appropriate.', 'No Deadline', '', ''),
(9, 'Resolution 5 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to further promote WMO recognition of long-term observing stations, to publish regular status reports and to issue another call for nomination of candidate stations in 2022;', 'EC-75', '', ''),
(10, 'Resolution 5 (EC-73)', 'EC-73', 'Members', 'Invites Members to further strengthen their efforts to achieve sustainable high-quality long-term Earth system observations, to continue collaborating on the WMO recognition mechanism and to promote it at the highest local, regional and national governmental levels, as appropriate.', 'No Deadline', '', ''),
(11, 'Resolution 6 (EC-73)', 'EC-73', 'SERCOM', 'Requests the Commission for Weather, Climate, Water and Related Environmental Services and Applications (SERCOM) to finalize the Sustainability Strategy for FFGS/WGC, in cooperation with the Hydrological Coordination Panel, Research Board and the relevant regional associations hosting FFGS Regional Centres for approval by Cg-Ext 2021;', 'No Deadline', '', ''),
(12, 'Resolution 6 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to support SERCOM in the necessary actions to finalize the Sustainability Strategy for the FFGS/WGC and undertake interim efforts to enhance the sustainability of the FFGS/WGC, if needed.', 'No Deadline', '', ''),
(13, 'Resolution 7 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General: \n(1) To arrange for the expeditious publication of the amended Technical Regulations, Volume II (WMO-No. 49);\n(2) To review, assisted by the president of SERCOM, and, as necessary, to publish updates to related WMO guidance material to ensure consistency with the amended Technical Regulations, Volume II (WMO-No. 49).', 'No Deadline', '', ''),
(14, 'Resolution 8 (EC-73)', 'EC-73', 'SERCOM', 'Requests SERCOM to develop appropriate mechanisms, in close coordination with INFCOM, the Research Board any other relevant WMO bodies, for the implementation of Integrated Health Services per Resolution 33 (Cg-18) - Advancing integrated health services, including clear links to the Global Framework for Climate Services (GFCS) especially with the Climate Services Information System (CSIS) and the Global Multi-Hazard Alert System (GMAS).', 'No Deadline', '', ''),
(15, 'Resolution 9 (EC-73)', 'EC-73', 'Members', 'Requests Members, regional associations and technical commissions to organize their activities to realize WIGOS goals and associated outcomes as described in the Plan;', 'No Deadline', '', ''),
(16, 'Resolution 9 (EC-73)', 'EC-73', 'RAs', 'Requests Members, regional associations and technical commissions to organize their activities to realize WIGOS goals and associated outcomes as described in the Plan;\nFurther requests Members to continue to provide resources, including through the WIGOS Trust Fund and/or seconded experts, to help support the implementation of WIGOS;', 'No Deadline', '', ''),
(17, 'Resolution 9 (EC-73)', 'EC-73', 'TCs', 'Requests Members, regional associations and technical commissions to organize their activities to realize WIGOS goals and associated outcomes as described in the Plan;', 'No Deadline', '', ''),
(18, 'Resolution 9 (EC-73)', 'EC-73', 'INFCOM', 'Requests the Commission for Observation, Infrastructure and Information Systems (INFCOM), to provide the technical lead in WIGOS operational activities;\n\nFurther requests INFCOM to keep the Plan under regular review, to update, and report progress in the implementation of the Plan to the Executive Council, and to submit a report to the Nineteenth World Meteorological Congress;', 'EC-75', '', ''),
(19, 'Resolution 9 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General:\n(1) To provide the necessary assistance and Secretariat support to Members and regional associations, especially to developing and least developed countries, for the further development of WIGOS through its operational phase, within available resources;\n(2) To consider allocating resources from the regular budget for the continued development of the WIGOS technical tools;\n(3) To motivate Members to contribute the necessary resources for the further development of the WIGOS technical tools;\n(4) To initiate a long-term planning — including the necessary allocation of resources — for the development of WIGOS tools in all WMO official languages, and for the operational sustainability of these tools;', 'No Deadline', '', ''),
(20, 'Resolution 9 (EC-73)', 'EC-73', 'Partners', 'Invites partners to participate in relevant implementation activities as specified in the Plan.', 'No Deadline', '', ''),
(21, 'Resolution 10 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General: \n(1) To publish the Technical Regulations, Volume I — General Meteorological Standards and Recommended Practices, (WMO-No. 49), Part I — WMO Integrated Global Observing System, and the Manual on the WMO Integrated Global Observing System (WMO-No. 1160), in all WMO official languages;\n(2) To ensure the editorial consistency of the relevant documents;\n(3) To bring the present resolution to the attention of all concerned;', 'No Deadline', '', ''),
(22, 'Resolution 10 (EC-73)', 'EC-73', 'INFCOM', 'Requests the Commission for Observation, Infrastructure and Information Systems to further develop and enhance the Technical Regulations, Volume I — General Meteorological Standards and Recommended Practices, (WMO-No. 49), Part I — WMO Integrated Global Observing System, and the Manual on the WMO Integrated Global Observing System (WMO-No. 1160) according to draft Resolution 9 (EC-73) — Plan for the WIGOS Initial Operational Phase (2020–2023).', 'No Deadline', '', ''),
(23, 'Resolution 11 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General:\n(1) To publish the updated Manual in all WMO official languages;\n(2) To ensure the editorial consistency of the relevant documents.', 'No Deadline', '', ''),
(24, 'Resolution 12 (EC-73)', 'EC-73', 'RAs', 'Requests the regional associations, respective Members and INFCOM to follow the RWCs Audit Process for all new nominations of RWCs, periodic evaluation, assessment and possible reconfirmation of RWCs;', 'No Deadline', '', ''),
(25, 'Resolution 12 (EC-73)', 'EC-73', 'Members', 'Requests the regional associations, respective Members and INFCOM to follow the RWCs Audit Process for all new nominations of RWCs, periodic evaluation, assessment and possible reconfirmation of RWCs;', 'No Deadline', '', ''),
(26, 'Resolution 12 (EC-73)', 'EC-73', 'INFCOM', 'Requests the regional associations, respective Members and INFCOM to follow the RWCs Audit Process for all new nominations of RWCs, periodic evaluation, assessment and possible reconfirmation of RWCs;\n\nRequests INFCOM, in collaboration with regional associations, to further develop the RWCs Audit Process as standard and recommended practices and procedures of the WMO Technical Regulations, Volume I - General Meteorological Standards and Recommended Practices, (WMO-No. 49).', 'No Deadline', '', ''),
(27, 'Resolution 13 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General:\n(1) To publish the Guide to the WMO Integrated Global Observing System (WMO-No. 1165) in all WMO official languages;\n(2) To ensure the editorial consistency of the relevant documents;', 'No Deadline', '', ''),
(28, 'Resolution 13 (EC-73)', 'EC-73', 'INFCOM', 'Requests the Commission for Observation, Infrastructure and Information Systems to further develop and enhance the Guide with additional material as it becomes available in accordance with draft Resolution 4.2(1)/1 (EC-73) - Plan for the WIGOS Initial Operational Phase (2020-2023);', 'No Deadline', '', ''),
(29, 'Resolution 13 (EC-73)', 'EC-73', 'Members', 'Invites Members:\n(1) To use the Guide to the WMO Integrated Global Observing System (WMO-No. 1165) in their implementation of the relevant Technical Regulations;\n(2) To provide feedback to the Secretary-General on how to improve subsequent versions of the Guide.', 'No Deadline', '', ''),
(30, 'Resolution 14 (EC-73)', 'EC-73', 'P/INFCOM', 'Requests the President of INFCOM to report to the Executive Council on further progress made on the implementation of WICAP;', 'No Deadline', '', ''),
(31, 'Resolution 14 (EC-73)', 'EC-73', 'RAs', 'Invites Regional Associations and their Members to continue towards the establishment of regional AMDAR programmes under WICAP in accordance with the WICAP Implementation Plan.', 'No Deadline', '', ''),
(32, 'Resolution 14 (EC-73)', 'EC-73', 'Members', 'Invites Regional Associations and their Members to continue towards the establishment of regional AMDAR programmes under WICAP in accordance with the WICAP Implementation Plan.', 'No Deadline', '', ''),
(33, 'Resolution 15 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General: \n(1) To publish the updated WMO-No. 8 in all WMO official languages;\n(2) To ensure the editorial consistency of the relevant documents;', 'No Deadline', '', ''),
(34, 'Resolution 15 (EC-73)', 'EC-73', 'Members', 'Invites Members to implement the Measurement Quality Classification for Surface Observing Stations on Land, and encourages them to share their experience with the implementation, to support the development of relevant guidance material.', 'No Deadline', '', ''),
(35, 'Resolution 16 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to publish the new edition of Volume III, to bring this resolution to the attention of Members and to ensure that provisions of General Regulation 100 are met;', 'No Deadline', '', ''),
(36, 'Resolution 16 (EC-73)', 'EC-73', 'Members', 'Invites Members to familiarize themselves with the amended provisions, bring them to the attention of relevant stakeholders and give effect to this resolution in accordance with General Regulation 101.', 'No Deadline', '', ''),
(37, 'Resolution 17 (EC-73)', 'EC-73', 'RAs', 'Requests the regional associations, respective Members and INFCOM to follow the RICs Process for all new nominations of RICs, and for assessment and periodic reconfirmation of existing RICs;\n\nRequests further the regional associations to examine the need and explore ways for beneficial use of the available RIC services by their Members;', 'No Deadline', '', ''),
(38, 'Resolution 17 (EC-73)', 'EC-73', 'Members', 'Requests the regional associations, respective Members and INFCOM to follow the RICs Process for all new nominations of RICs, and for assessment and periodic reconfirmation of existing RICs;\n\nUrges Members hosting RICs without accreditation, to achieve ISO/IEC 17025 accreditation, at their earliest convenience;', 'No Deadline', '', ''),
(39, 'Resolution 17 (EC-73)', 'EC-73', 'INFCOM', 'Requests the regional associations, respective Members and INFCOM to follow the RICs Process for all new nominations of RICs, and for assessment and periodic reconfirmation of existing RICs;\n\nRequests INFCOM, in collaboration with regional associations, to further develop the RICs audit process as standard and recommended practices and procedures of the WMO Technical Regulations, Volume I - General Meteorological Standards and Recommended Practices (WMO-No. 49).', 'No Deadline', '', ''),
(40, 'Resolution 18 (EC-73)', 'EC-73', 'Members', 'Requests Members, regional associations and technical commissions to organize their activities to realize the goals and associated outcomes as described in the Plan;\n\nUrges Members:\n(1) To continue fostering engagements of their National Meteorological and Hydrological Services (NMHSs) with national and regional partners, research institutions, and academia, to maximize the benefit of existing observing infrastructure, of the access to cryospheric data, and of the transfer of advances in cryospheric research to operations;\n(2) To continue to provide resources, including through the GCW Trust Fund and/or seconded experts, to help support the implementation of GCW;', 'No Deadline', '', ''),
(41, 'Resolution 18 (EC-73)', 'EC-73', 'RAs', 'Requests Members, regional associations and technical commissions to organize their activities to realize the goals and associated outcomes as described in the Plan;\n\nRequests the regional associations to collaborate with GCW in the organization of workshops, including capacity development and outreach activities;', 'No Deadline', '', ''),
(42, 'Resolution 18 (EC-73)', 'EC-73', 'TCs', 'Requests Members, regional associations and technical commissions to organize their activities to realize the goals and associated outcomes as described in the Plan;', 'No Deadline', '', ''),
(43, 'Resolution 18 (EC-73)', 'EC-73', 'INFCOM', 'Requests the Commission for Observation, Infrastructure and Information Systems (INFCOM), to provide the technical lead of GCW activities and liaise with the appropriate bodies and partners;\n\nRequests further INFCOM to keep the Plan under regular review, to update, and report progress in the implementation of the Plan to the Executive Council, and to submit a report to the Nineteenth Session of the World Meteorological Congress;', 'EC-75', '', ''),
(44, 'Resolution 18 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General:\n(1) To provide the necessary assistance and Secretariat support to Members, especially to developing and least developed countries, for the implementation of the Plan reflective of their requirements and within available resources;\n(2) To motivate Members to contribute the necessary resources for the further development of the activities of the Plan;', 'No Deadline', '', ''),
(45, 'Resolution 18 (EC-73)', 'EC-73', 'Partners', 'Invites partner organizations:\n(1) To participate in relevant activities of the Plan;\n(2) To further support the implementation of GCW by contributing with human and financial resources.', 'No Deadline', '', ''),
(46, 'Resolution 19 (EC-73)', 'EC-73', 'INFCOM', 'Requests the Commission for Observations, Infrastructure and Information Systems (INFCOM) to:\n(1) Develop in coordination with the Commission for Weather, Climate, Water and Related Environmental Services and Applications (SERCOM), the most efficient arrangements for the collection and publication of Climatological Standard Normals;\n(2) Coordinate and monitor the data exchange in accordance with the requirements provided by SERCOM and making best use of national, regional and global WMO Information System (WIS) infrastructure;\n(3) Report to the Executive Council on the progress of the implementation of the data collection;', 'No Deadline', '', ''),
(47, 'Resolution 19 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to facilitate the collection and publication of Climatological Standard Normals 1991–2020;', 'No Deadline', '', ''),
(48, 'Resolution 19 (EC-73)', 'EC-73', 'Members', 'Urges Members to:\n(1) Start calculating and publishing 1991–2020 Climatological Standard Normals nationally as soon as possible with the aim of completion of the Global Climate Normals (CLINO) (WMO-No. 847), ideally not later than the end of 2023;\n(2) Launch a broad communication campaign to promote the updated Climatological Standard Normals and to clearly communicate Normals’ update implications to National Meteorological and Hydrological Services (NMHSs) internal and external users as well as the general public;\n(3) Contribute to the WMO collection of Climatological Standard Normals 1991–2020.', 'No Deadline', '', ''),
(49, 'Resolution 20 (EC-73)', 'EC-73', 'INFCOM', 'Requests\n(1) The Commission for Observation, Infrastructure and Information Systems (INFCOM) to review the cross-cutting documents relating to information management and to lead the process for future updates of the Manual as part of its role in promoting technical regulations underpinning data management;', 'No Deadline', '', ''),
(50, 'Resolution 20 (EC-73)', 'EC-73', 'SERCOM', 'Requests\n(2) The Commission for Weather, Climate, Water and Related Environmental Services & Applications (SERCOM) to assess progress made by Members in benefiting from these regulations in their climate activities and identify new data requirements for climate activities;', 'No Deadline', '', ''),
(51, 'Resolution 20 (EC-73)', 'EC-73', 'SG', 'Also requests the Secretary-General:\n(1) To publish the amended Manual;\n(2) To facilitate collaboration among Members and resource mobilization for assisting countries in applying the standard and recommended practices stipulated in the Manual;', 'No Deadline', '', ''),
(52, 'Resolution 20 (EC-73)', 'EC-73', 'Members', 'Urges Members to make their best efforts in assessing the maturity of their climate datasets based on these provisions and relevant guidelines attached to the Manual.', 'No Deadline', '', ''),
(53, 'Resolution 21 (EC-73)', 'EC-73', 'SERCOM', 'Requests the Commission for Observation, Infrastructure and Information Systems (INFCOM) in close collaboration with the Commission for Weather, Climate, Water and Related Environmental Services and Applications (SERCOM) to: (1) Lead the development and implementation of OpenCDMS in accordance with the Climate Data Management System Specifications (WMO-No. 1131);\n(2) Ensure that OpenCDMS design principles are consistent with the WMO Information System (WIS) 2.0 Strategy; (3) Update the Climate Data Management System Specifications (WMO-No. 1131) in relation to Information Management;', 'No Deadline', '', ''),
(54, 'Resolution 21 (EC-73)', 'EC-73', 'Members', 'Urges Members to support the evolution of OpenCDMS by providing expertise, human and financial resources;', 'No Deadline', '', ''),
(55, 'Resolution 21 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to support the development and implementation of OpenCDMS and assist in mobilizing financial resources for the relevant expert work and for the development of CDMS and implementation activities.', 'No Deadline', '', ''),
(56, 'Resolution 22 (EC-73)', 'EC-73', 'Members', 'Urges Members: \n(1) To analyse WIS 2.0 Functional Architecture, provided in Annex 2 to this resolution, and participate proactively in the WIS 2.0 development and implementation projects, an updated list of which is provided at https://community.wmo.int/activity-areas/wis/wis2-demonstration-projects;\n(2) To support WIS development through secondments and additional funding to the WIS Trust Fund.', 'No Deadline', '', ''),
(57, 'Resolution 23 (EC-73)', 'EC-73', 'P/INFCOM', 'Requests the president of the Commission for Observation, Infrastructure and Information Systems, in close collaboration with the president of the Commission for Weather, Climate, Water and Related Environmental Services and Applications, to make proposals in further integrating Climate Service Information System (CSIS) aspects into the GDPFS;', 'No Deadline', '', ''),
(58, 'Resolution 24 (EC-73)', 'EC-73', 'P/INFCOM', 'Requests the president of INFCOM, in close coordination with the president of the Commission for Weather, Climate, Water and Related Environmental Services and Applications, the presidents of the regional associations and other relevant bodies, as part of the ongoing WMO reform process, to review the role of the regional associations in the designation of Global Data-processing and Forecasting System Centres within the next update cycle of the Manual on the Global Data-processing and Forecasting System (WMO-No. 485);', 'No Deadline', '', ''),
(59, 'Resolution 25 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to publish the revised Rules of Procedure.', 'No Deadline', '', ''),
(60, 'Resolution 26 (EC-73)', 'EC-73', 'JCB', 'Requests:\n(1) The JCB to conform to the Rules of Procedure in the conduct of its business;', 'No Deadline', '', ''),
(61, 'Resolution 26 (EC-73)', 'EC-73', 'SG', 'Requests:\n(2) The Secretary-General to publish the above-mentioned rules of procedure in the Rules of Procedure for the Executive Council (WMO-No. 1256);', 'No Deadline', '', ''),
(62, 'Resolution 26 (EC-73)', 'EC-73', 'TCC', 'Requests:\n(3) The TCC to keep under review the rules of procedure in consultation with IOC/UNESCO.', 'No Deadline', '', ''),
(63, 'Resolution 28 (EC-73)', 'EC-73', 'JCB', 'Requests the JCB to continue working across WMO and IOC to identify and promote priority implementation actions in support of the WMO-IOC Collaborative Strategy;', 'No Deadline', '', ''),
(64, 'Resolution 28 (EC-73)', 'EC-73', 'SG', 'Also requests the Secretary-General to publish the above strategy and to provide the necessary secretariat support for the JCB to implement;\n\nFurther requests the Secretary-General to facilitate the development of a WMO Ocean Implementation Plan to focus on the varied ocean activities at WMO, in alignment with the WMO Strategic and Operational Plans, and as a follow-up to the WMO Ocean Collaborative Framework (Resolution 65 (Cg-18));', 'No Deadline', '', ''),
(65, 'Resolution 28 (EC-73)', 'EC-73', 'SERCOM', 'Requests SERCOM, INFCOM, the Research Board, regional associations, the Capacity Development Panel and any other relevant subsidiary body, to identify priority implementation actions in support of developing a Draft WMO Ocean Implementation Plan;', 'No Deadline', '', ''),
(66, 'Resolution 28 (EC-73)', 'EC-73', 'INFCOM', 'Requests SERCOM, INFCOM, the Research Board, regional associations, the Capacity Development Panel and any other relevant subsidiary body, to identify priority implementation actions in support of developing a Draft WMO Ocean Implementation Plan;', 'No Deadline', '', ''),
(67, 'Resolution 28 (EC-73)', 'EC-73', 'RB', 'Requests SERCOM, INFCOM, the Research Board, regional associations, the Capacity Development Panel and any other relevant subsidiary body, to identify priority implementation actions in support of developing a Draft WMO Ocean Implementation Plan;', 'No Deadline', '', ''),
(68, 'Resolution 28 (EC-73)', 'EC-73', 'RAs', 'Requests SERCOM, INFCOM, the Research Board, regional associations, the Capacity Development Panel and any other relevant subsidiary body, to identify priority implementation actions in support of developing a Draft WMO Ocean Implementation Plan;', 'No Deadline', '', ''),
(69, 'Resolution 28 (EC-73)', 'EC-73', 'CDP', 'Requests SERCOM, INFCOM, the Research Board, regional associations, the Capacity Development Panel and any other relevant subsidiary body, to identify priority implementation actions in support of developing a Draft WMO Ocean Implementation Plan;', 'No Deadline', '', ''),
(70, 'Resolution 28 (EC-73)', 'EC-73', 'Members', 'Invites Members concerned to:\n(1) Consider nationally focussed ocean activities, collaborating across multiple agencies with a regional co-operation approach [S. Tajbakhsh Mosalman].\n(2) Contribute to the UN Decade of Ocean Science for Sustainable Development 2021–2030.', 'No Deadline', '', ''),
(71, 'Resolution 29 (EC-73)', 'EC-73', 'CCP', 'Requests the CCP to continue to explore the mechanisms to enhance the visibility, effectiveness and implementation of the Global Framework for Climate Services (GFCS), including the feasibility of co-sponsorship of the GFCS, to initiate preparation of an implementation plan for the GFCS post-2022, and to keep the Executive Council (EC) informed on progress through reports in subsequent sessions.', 'No Deadline', '', ''),
(72, 'Resolution 30 (EC-73)', 'EC-73', 'INFCOM', 'Requests INFCOM, the Commission for Weather, Climate, Water and Related Environmental Services and Applications (SERCOM) and the Research Board to integrate in their respective work programmes the technical, operational, and research priorities and activities previously under the remit of EC-PHORS;\n\nAlso requests INFCOM, SERCOM and the Research Board to develop together a roadmap for science-to-services from the Polar Prediction Project of WWRP, leading to the integration of its outcomes via the Global Data Processing and Forecasting System (GDPFS) and on identifying new research priorities, including by contributing to the vision to be developed by the Science Advisory Panel;', 'No Deadline', '', ''),
(73, 'Resolution 30 (EC-73)', 'EC-73', 'SERCOM', 'Requests INFCOM, the Commission for Weather, Climate, Water and Related Environmental Services and Applications (SERCOM) and the Research Board to integrate in their respective work programmes the technical, operational, and research priorities and activities previously under the remit of EC-PHORS;\n\nAlso requests INFCOM, SERCOM and the Research Board to develop together a roadmap for science-to-services from the Polar Prediction Project of WWRP, leading to the integration of its outcomes via the Global Data Processing and Forecasting System (GDPFS) and on identifying new research priorities, including by contributing to the vision to be developed by the Science Advisory Panel;', 'No Deadline', '', ''),
(74, 'Resolution 30 (EC-73)', 'EC-73', 'RB', 'Requests INFCOM, the Commission for Weather, Climate, Water and Related Environmental Services and Applications (SERCOM) and the Research Board to integrate in their respective work programmes the technical, operational, and research priorities and activities previously under the remit of EC-PHORS;\n\nAlso requests INFCOM, SERCOM and the Research Board to develop together a roadmap for science-to-services from the Polar Prediction Project of WWRP, leading to the integration of its outcomes via the Global Data Processing and Forecasting System (GDPFS) and on identifying new research priorities, including by contributing to the vision to be developed by the Science Advisory Panel;', 'No Deadline', '', ''),
(75, 'Resolution 30 (EC-73)', 'EC-73', 'EC-PHORS', 'Requests EC-PHORS:\n(1) To submit to the Executive Council recommendations on an effective role to oversee, coordinate and monitor how polar and high-mountain observations, research, services and policies are developed and implemented within and externally to WMO, to ensure that WMO continues to play a meaningful role on these matters; \n(2) To submit to the Policy Advisory Committee (as per schedule in EC-73/Doc. 5.3) recommendations informing the WMO strategic objectives for 2024-2027 on specific priorities for polar and high mountain regions, as developed with input from other WMO bodies and reflecting the necessary strategic engagements;', 'No Deadline', '', ''),
(76, 'Resolution 30 (EC-73)', 'EC-73', 'Members', 'Invites Members:\n(1) To nominate candidates for members of EC-PHORS, at a level reflective of their polar (Arctic, Antarctic) and high-mountain strategic priorities and goals,\n(2) To contribute to the development of EC-PHORS strategic recommendations to the Executive Council and the Policy Advisory Committee, by reflecting their Arctic, Antarctica, and high-mountain priorities, aligned with the WMO strategic goals;', 'No Deadline', '', ''),
(77, 'Resolution 31 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to:\n(1) Transmit the financial statements together with his report and the report of the External Auditor thereon to all Members of the World Meteorological Organization;\n(2) Prepare a detailed management action plan with responsibilities and timelines for addressing the 21 recommendations of the external auditor for review and discussion at the 74th session of Executive Council; and\n(3) Report progress on the implementation of the management action plan to the Audit and Oversight Committee and the Executive Council;', 'EC-74', '', ''),
(78, 'Resolution 32 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General:\n(1) To provide EC-74 with a progress report regarding the findings and plans for applying efficiencies gained in support of the Strategic Plan and budget execution;\n(2) To include a region-specific analysis and summary of activities, particularly in relation to LTG 4 but also in relation to other LTGs, in performance reporting on the Strategic and Operating Plans to the 74th session of the Executive Council (EC-74);\n(3) To ensure that the trend of the relevant expenditure items in absolute and relative terms since recent years, as well as information related to the status of long-term liabilities and related funding, should be presented, when submitting future draft budgets to the Executive Council;', 'EC-74', '', ''),
(79, 'Resolution 33 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to make available the text of the in-force resolutions and decisions of the Council, including those with corrigenda in a new issue of Resolutions and Decisions of Congress and the Executive Council (WMO-No. 508).', 'No Deadline', '', ''),
(80, 'Resolution 33 (EC-73)', 'EC-73', 'TCC', 'Requests the Technical Coordination Committee and the Policy Advisory Committee to undertake a review of the resolutions and decisions of Congress and the Executive Council kept in force, on technical and strategic matters respectively, with a view to submitting to the Council in 2022 recommendations on their possible consolidation, for final consideration by the nineteenth Congress (Cg-19).', 'EC-75', '', ''),
(81, 'Resolution 33 (EC-73)', 'EC-73', 'PAC', 'Requests the Technical Coordination Committee and the Policy Advisory Committee to undertake a review of the resolutions and decisions of Congress and the Executive Council kept in force, on technical and strategic matters respectively, with a view to submitting to the Council in 2022 recommendations on their possible consolidation, for final consideration by the nineteenth Congress (Cg-19).', 'EC-75', '', ''),
(82, 'Decision 3 (EC-73)', 'EC-73', 'President', '(5) To request the President and the Secretary-General to finalize the arrangements for the organization of Cg-Ext. 2021, guided by the tentative schedule in Annex 2, taking into account the experience from online sessions of constituent bodies held so far and ensuring, as far as possible, equitable participation of all Members in light of time zone differences and adequate time for debate as well as informal consultations;', 'No Deadline', '', ''),
(83, 'Decision 3 (EC-73)', 'EC-73', 'SG', '(5) To request the President and the Secretary-General to finalize the arrangements for the organization of Cg-Ext. 2021, guided by the tentative schedule in Annex 2, taking into account the experience from online sessions of constituent bodies held so far and ensuring, as far as possible, equitable participation of all Members in light of time zone differences and adequate time for debate as well as informal consultations;\n(6) To request the Secretary-General to develop the methods of work for online sessions of Congress similar to those applied to the sessions of constituent bodies during 2020 and 2021 in full respect of the provisions of the WMO Convention and General Regulations.', 'No Deadline', '', ''),
(84, 'Decision 4 (EC-73)', 'EC-73', 'PAC', '(3) To further request:\n(a) The Policy Advisory Committee (PAC) to provide input to the objectives, scope and overall approach of the external and independent evaluation, including addressing the full scope of JIU Recommendation 1 (JIU/REP/2021/1), and to endorse a list of Key Performance Indicators (KPIs) that would allow continued assessment of the reform in the longer term;\n(4) To note the initial evaluation approach, elements and timeline presented in the annex to the present decision, which the PAC will continue to review and adjust in light of the JIU recommendation, and request the Secretary-General to develop a Tentative Work Plan, with specific dates, tasks and deliverables;', 'No Deadline', '', ''),
(85, 'Decision 4 (EC-73)', 'EC-73', 'SG', '(3) To further request:\n(b) The Secretary-General (i) to continue monitoring the set of indicators on the reform, such as composition and diversity of the new constituent bodies and their working structures, including the effectiveness of the Secretariat reform, (ii) to design additional KPIs, collect quantitative data, and conduct a comparative analysis, including on the cost of the constituent body reform, to complement and support the evaluation; and (iii) to embed the KPIs endorsed by PAC in the Organization’s monitoring system;\n(4) [...] request the Secretary-General to develop a Tentative Work Plan, with specific dates, tasks and deliverables;', 'No Deadline', '', ''),
(86, 'Decision 5 (EC-73)', 'EC-73', 'SG', '(7) To request the Secretary-General, in accordance with EC-73 guidance and in consultation with the presidents and chairs of concerned WMO bodies, to finalize the above programme and schedule and review it based on the evolution of COVID-19;\n(10) To request the Secretary-General, all constituent and all subsidiary bodies to regularly assess measures taken in the light of the COVID-19 pandemic in the operation of WMO, such as meetings and capacity development activities, and use assessment results to enhance the effectiveness of WMO responses and ensure that the Organization could better adapt to the new circumstances;', 'No Deadline', '', ''),
(87, 'Decision 5 (EC-73)', 'EC-73', 'SG', '(11) To request the Secretary-General to submit to the seventy-fourth session of the Executive Council (EC-74) for its consideration:\n(a) An updated report on the impact of COVID-19 on the delivery of the Operating Plan for 2021;\n(b) A revised Operating Plan for 2022, based on the approved biennial budget for 2022–2023, taking into account the impacts of COVID-19;\n(c) The finalized programme and schedule of sessions of constituent bodies, the Research Board and subsidiary bodies of the Executive Council;\n(d) Guidance for the Members on how to maintain the operation and maintenance of systems, in line with health and safety protocols which could help members sustain the quality performance of the system. ', 'EC-74', '', ''),
(88, 'Decision 5 (EC-73)', 'EC-73', 'SG', '(12) To request the Secretary-General to submit to the seventy-fifth session of the Executive Council (EC-75) for its consideration:\n(a) An updated report on the impact of COVID-19 on the delivery of the Operating Plan for 2022;\n(b) An updated report and plan for meetings through the remainder of the financial period taking into account EC-74 guidance.', 'EC-75', '', ''),
(89, 'Decision 7 (EC-73)', 'EC-73', 'INFCOM', 'Requests INFCOM to further develop the WIGOS Indicators, including additional criteria to allow an improved assessment of Members’ compliance with WIGOS implementation and with WIGOS standards and recommendations, along with the implementation of the Plan for the WIGOS Initial Operational Phase (2020–2023);', 'No Deadline', '', ''),
(90, 'Decision 7 (EC-73)', 'EC-73', 'SG', 'Requests the Secretary-General to provide Members with regular assessment results of the progress achieved in the national implementation of WIGOS, based on the WIGOS Indicators;', 'No Deadline', '', ''),
(91, 'Decision 7 (EC-73)', 'EC-73', 'Members', 'Urges Members to collaborate with the Secretary-General in this assessment.', 'No Deadline', '', ''),
(92, 'Decision 9 (EC-73)', 'EC-73', 'CDP', 'The Executive Council decides to request the Capacity Development Panel to:\n(1) Compile best practices from the National Meteorological and Hydrological Services (NMHSs) considered to be meeting global standards in the delivery of services. This compilation to be used in promotion of standards worldwide;\n(2) Review and update the current competency frameworks as needed, and support development of new frameworks in areas not yet addressed, with the support of the Education and Training Office, as well as to benefit from the experiences and knowledge of the WMO Regional Training Centres (RTCs), in close coordination with the RTC Representatives;\n(3) Coordinate the Technical Commissions (SERCOM and INFCOM) in the development of a capacity development framework to support the implementation of their activities;\n(4) Develop strategies to foster engagement of academic and NMHS staff at the local and regional levels on research and research transfer to operation and application to improved operational delivery;\n(5) Make recommendations on how to foster systematic contributions from academia towards the enhancement of the research-to-operation continuum which should address approaches to promotion of capacity development and service capabilities in weather, climate, hydrology and environment at regional levels through promotion of collaboration and communication between regional centres such as Regional Specialized Centres, Regional Climate Centres, Regional Training Centres (RTCs), WMO Integrated Global Observing System (WIGOS) Centres and WMO Information System (WIS) Centres, etc;\n(6) Support and review a publication on leadership and management, based on experiences gained from the training programmes jointly organized by WMO and the Meteorological Service Singapore for Regional Associations II and V since 2018;\n(7) Support the development of guidelines on best practices for NMHSs in terms of working with development partners/Government, with the aim of enhancing knowledge sharing in this area;\n(8) Contribute to the Fourteenth WMO Education and Training Symposium (SYMET-14) which will be held in 2021, as additional input to the development and promotion of education and training activities of the Organization;\n(9) Conduct an assessment of and clarify the roles and responsibilities of the different actors as well as the different funding mechanisms involved in WMO Capacity Development activities.\n(10) Make recommendations and develop strategies to strengthen sub-regional, regional, inter-regional and other cooperation groups in order to establish synergies at inter-regional and sub-regional levels and to identify training and capacity-building opportunities;', 'No Deadline', '', ''),
(93, 'Decision 9 (EC-73)', 'EC-73', 'SG', 'The Executive Council also decides to request the Secretary-General to:\n(11) Ensure continued Secretariat support to the activities of the Capacity Development Panel as it works to achieve its objectives within the limit of affordable financial resources;\n(12) Encourage the centralization and dissemination of information concerning regional capacity development opportunities and capacity development opportunities for NMHSs, such as training courses and so forth, provided by various actors;\n(13) Support NHMSs to access funding for capacity development through international cooperation mechanisms, such as projects, fellowships, internships, and so forth.', 'No Deadline', '', ''),
(94, 'Decision 10 (EC-73)', 'EC-73', 'P/INFCOM', '(7) Requests the president of INFCOM, in collaboration with the presidents of regional associations, to widely publicize the call for the next award for the Professor Dr Vilho Väisälä Award for the Development and Implementation of the Instruments and Methods of Observation in Developing Countries, towards ensuring a larger number of submissions from developing countries, and to recognize the important work being performed in developing countries in the field of instruments and methods of observation for the WMO Integrated Global Observing System (WIGOS).', 'No Deadline', '', ''),
(95, 'Decision 11 (EC-73)', 'EC-73', 'Members', '(2) To invite Members to undertake activities to celebrate World Meteorological Day in 2022 using the above theme based on background information published by the Secretary-General.', 'No Deadline', '', ''),
(96, 'Decision 13 (EC-73)', 'EC-73', 'PAC', '(3) To request the Policy Advisory Committee (PAC), according to the schedule provided in the annex, to:\n(a) Review the Strategic Plan and update, as needed, the focus areas for 2024-2027 considering the state and evolution of key drivers, Organization performance on the WMO Strategic Plan 2020-2023 and contributions from constituent and advisory bodies; and\n(b) Submit a draft Strategic Plan 2024-2027 to the Executive Council in 2022 for its consideration and subsequent submission to the Nineteenth World Meteorological Congress;', 'EC-75', '', ''),
(97, 'Decision 16 (EC-73)', 'EC-73', 'Members', '(2) To invite Members to consider voluntary contributions to the WMO Trust Fund for Extraordinary Works at the WMO Building in order to fully finance the required works as explained in EC-73/INF. 6.4,', 'No Deadline', '', ''),
(98, 'Decision 16 (EC-73)', 'EC-73', 'SG', '(3) To request the Secretary-General to take the necessary actions, and allocate resources, to ensure that the building remains a safe, secure, efficient and environmentally friendly work environment for the entire WMO community.', 'No Deadline', '', ''),
(99, 'Decision 17 (EC-73)', 'EC-73', 'SG', '(2) To request the Secretary-General:\n(a) To continue providing support to the JIU in its work and to implement its recommendations in a timely manner;\n(b) To expand on the initial WMO management action plan by preparing an annex containing detailed responsibilities, milestones, prioritization, financial implications and progress indicators that implement all four formal recommendations, as well as addressing the 27 informal recommendations in JIU/REP/2021/1. A first phase of review should be carried out at EC-74 in October 2021 followed by a more comprehensive review at EC-75 in 2022;\n(c) To provide a six-monthly update to the Executive Council, aligned to the sessions of the AOC, reporting on the progress made in implementation of all four formal recommendations, as well as in addressing the 27 informal recommendations in JIU/REP/2021/1, including comments from the AOC on the progress as part of their routine reporting to Executive Council;\n(d) That the next cycle of audits and evaluations include a specific focus on the effectiveness of management controls in order for Executive Council to assess whether the JIU, the External Auditor, and the Audit and Oversight Committee recommendations have been addressed;', 'EC-74, EC-75', '', ''),
(100, 'Decision 17 (EC-73)', 'EC-73', 'President', '(2) To request the President:\n(a) To form an intersessional EC Working Group to establish the framework of legislative changes necessary to meet the requirements of Recommendation 7 of JIU/REP/2020/1. The progress of this EC Working Group shall be communicated to the Policy Advisory Committee (PAC) and to be further considered by EC-75 in 2022 to be presented to WMO Congress in 2023 for approval;\n(b) To prepare an agenda item for Executive Council on Secretariat restructuring, including updates from the management action plan and lessons learnt.', 'EC-75', '', ''),
(101, 'Resolution 1 (EC-74)', 'EC-74', 'PAC', 'The Executive Council,\nRequests the Policy Advisory Committee and Selection Committee of the IMO Prize to keep the IMO Prize Nomination and Selection Criteria and Process under review and submit updates as necessary, to ensure improved balance and diverse selection criteria and process.', 'No Deadline', '', ''),
(102, 'Resolution 1 (EC-74)', 'EC-74', 'IMO Prize Selection Committee', 'The Executive Council,\nRequests the Policy Advisory Committee and Selection Committee of the IMO Prize to keep the IMO Prize Nomination and Selection Criteria and Process under review and submit updates as necessary, to ensure improved balance and diverse selection criteria and process.', 'No Deadline', '', ''),
(103, 'Decision 2 (EC-74)', 'EC-74', 'SG', 'The Executive Council,\n(3) Requests the Secretary-General:\n(a) To implement adjustments to the Operating Plan for 2022 that seek to mitigate the negative effects of COVID-19 whilst ensuring progress, to the extent possible, on the Strategic Objectives, and present the Plan to EC-75;\n(b) To provide EC-75 with a report and assessment of the efficacy of the measures implemented to mitigate the impacts of COVID-19 on the functioning of the Organization; \n(c) To ensure the publication of WMO mandatory publications defined by Resolution 81 (Cg-18) - WMO mandatory publications and distribution policy for the eighteenth financial period, in all the official languages of the Organization;\n(d) To explore the feasibility of establishing videoconference hubs located at regional offices and other relevant UN locations (regional economic commissions) to support the participation of delegates and experts in virtual and hybrid meetings, and inform EC-75;', 'EC-75', '', ''),
(104, 'Decision 2 (EC-74)', 'EC-74', 'Members', 'The Executive Council,\n(4) Invites Members to refer to the information on the impacts of COVID-19 on the WMO community and mitigation actions to support business continuity during the pandemic provided in the Cg-Ext(2021)/INF. 4.3 and the COVID-19 WMO Survey of Members, Initial Impact Report circulated to Members (00946/2021/S/DPS) in anticipation of a more developed business continuity policy/strategy and plan, as requested by Resolution 4.3/1 (Cg-Ext(2021)) - Towards a structured WMO response to global crises, to be reviewed by the Executive Council for presentation at the World Meteorological Congress at its nineteenth session (Cg-19) in 2023.', 'EC-75', '', ''),
(105, 'Decision 3 (EC-74)', 'EC-74', 'SG', 'The Executive Council decides:\n(3) To request the Secretary-General to add any future recommendations from the External Auditor to the Management Action Plan, to prioritise actions in consultation with the External Auditor and the Audit and Oversight Committee and to present the Management Action Plan to the Executive Council each year alongside the annual financial statements.', 'Each year', '', '');
